import Layout from "../components/Layout"
import { loadStripe } from "@stripe/stripe-js";
import { CardElement, Elements, useStripe } from "@stripe/react-stripe-js";

const stripePromise = loadStripe("pk_test_51N3fJlLtUNz4iAH5eA4TrPtlICbtNyyIDTUoJ0gg4zGmNLrN2biG59zFBgQyBcUapodjFZbAC1H2LDhwl31Uil1K00ehLbFsuR");

const CheckoutForm = ({amount}) => {
    const stripe = useStripe();

    const submitHandler = (e) => {
        e.preventDefault();
        stripe.createPaymentMethodz({
            type: 'card',
            card: CardElement
        })
        .then(PaymentMethod => {
            console.log("PaymentMethod:", PaymentMethod);
        })
        .catch(error => {
            console.log("error:", error);
        })
    }

    return (
        <form onSubmit={submitHandler}>
            <div className="py-4">
                <CardElement />
            </div>
            <button className="btn btn-success">Pay Now</button>
        </form>
    )
}

const CheckoutView = () => {
    let amount = 0;
    let cart = localStorage.getItem('cart');
    if (cart) {
        cart = JSON.parse(cart);
        cart.forEach((cartItem) => {
            amount += cartItem.price * cartItem.quantity;
        });
    }

    return (
        <Layout title="Checkout" description="">
            <section className="py-5">
                <div className="container">
                    <h2>Amount: $(amount)</h2>
                    <Elements stripe={stripePromise}>
                        <CheckoutForm amount={amount} />
                    </Elements>
                </div>
            </section>
        </Layout>
    )
}

export default CheckoutView;
