import { useState } from 'react';
import Layout from "../components/Layout"
import productList from "../dummy/productList";
import ProductCard from "../components/ProductCard";

const HomeView = (props) => {
    const [cart, setCart] = useState([]);

    const addCartItem = (cartItem) => {
        setCart([...cart, cartItem]);
    };

    const removeCartItem = (idx) => {
        const newCart = [...cart].splice(idx, 1);
        setCart(newCart);
    };

    const productCards = productList.map((product) => {
        return <ProductCard 
            key={product.id} 
            product={product} 
            addCartItem={addCartItem}
        />
    });

    let totalPrice = 0;
    const cartTableRows = cart.map((cartItem, idx) => {
        totalPrice += cartItem.price * cartItem.quantity;
        return (
            <tr>
                <td className="btn btn-danger btn-sm me-2">
                    <button onClick={() => removeCartItem(idx)}>&times;</button>
                    {cartItem.name}
                </td>
                <td>${cartItem.price}</td>
                <td>{cartItem.quantity}</td>
                <td>${cartItem.price * cartItem.quantity}</td>
            </tr>
        )
    });

    const cartElement = cart.length > 0 ? (
        <div>
            <h2>Cart</h2>
            <table className="table">
                <thead>
                    <tr>
                        <th>Name</th>
                        <th>Price</th>
                        <th>Quantity</th>
                        <th>Total</th>
                    </tr>
                </thead>
                <tbody>
                    {cartTableRows}
                </tbody>
                <tfoot>
                    <tr>
                        <th colSpan="3">Total</th>
                        <th>${totalPrice}</th>
                    </tr>
                </tfoot>
            </table>
        </div>
    ) : null;

    return (
        <Layout
            title="我的網站"
            description="這是我的網站"
            darkMode={true}
        >
            <section className="py-5">
                <div className="container">
                    <h2>Home View Content</h2>
                    <div className="row my-4">
                        {productCards}
                    </div>
                </div>
            </section>
        </Layout>
    )
}

export default HomeView
